import {
    CHANGE_SEARCH_FIELD,
    REQUEST_SEARCH_ROBOTS_SUCCESS,
    REQUEST_SEARCH_ROBOTS_PENDING,
    REQUEST_SEARCH_ROBOTS_FAILED
} from "./Constants";

export const setSearchField = (text) => ({
        type: CHANGE_SEARCH_FIELD,
        payload: text
    }
)

export const requestRobots = (dispatch)=>{
    dispatch({type: CHANGE_SEARCH_ROBOTS_PENDING});
    fetch('https://jsonplaceholder.typicode.com/users')
        .then( res => res.json())
        .then(data => dispatch({type:REQUEST_SEARCH_ROBOTS_SUCCESS, payload: data}))
        .catch(error =>dispatch({ type: REQUEST_SEARCH_ROBOTS_SUCCESS, payload: error}))
}
